mapper.insert:
INSERT INTO wiki(parent_wiki_no, study_no, title, ordr, member_no) VALUES (
NULL, #{parentWikiNo},
#{studyNo}, #{title}, #{ordr}, #{memberNo});

mapper.select:
SELECT wiki_no, parent_wiki_no, title, ordr
FROM wiki
WHERE wiki_no = #{wikiNo};

mapper.update:
UPDATE wiki SET parent_wiki_no =
NULL, #{parentWikiNo},
title = #{title}, ordr = #{ordr} WHERE wiki_no = #{wikiNo}
