mapper.select:
SELECT wiki_no, title, ordr, layer, layer_ordr
FROM wiki
WHERE study_no = #{studyNo}
ORDER BY ordr;

mapper.insert:
INSERT INTO
  wiki(wiki_no, study_no, member_no, title, content)
VALUES(#{wikiNo}, #{studyNo}, 1, #{title}, #{content});

